script:

	function createCloud() {
    	var tagCloud = new TagCloud();
    	var index = 0;
      	tagCloud.setValues([
      	<#list nxthemesInfo.model.items as item>
      		{val:#{item.weight},tag:#{item.label},href:#{item.href}}
      		if(index < nxthemesInfo.model.items.size - 1) { 
      			,;
      		}                         
      		index++;
        </#list>]);                  
      	tagCloud.create('tagsCloud');
      	${item.label}
	}
